import re
from typing import Union
from langflow.custom import Component
from langflow.inputs import MessageTextInput
from langflow.schema.message import Message
from langflow.template import Output
from langflow.schema import Data
import langwatch

class LangWatchEvaluatorComponent(Component):
    display_name = "LangWatch Evaluator"
    description = "Evaluates a question-answer pair using LangWatch and provides a trace URL."
    icon = "view"

    inputs = [
        MessageTextInput(
            name="answer",
            display_name="Chat Output",
            info="The generated answer to be evaluated.",
        ),
        MessageTextInput(
            name="context_data",
            display_name="Context Data",
            info="The data to be used as context for evaluation.",
        ),
        MessageTextInput(
            name="user_email",
            display_name="User Email",
            info="The user ID for the trace metadata.",
        ),
        MessageTextInput(
            name="user_name",
            display_name="Participant Name",
            info="Full name for identification in the trace metadata.",
        ),
    ]

    outputs = [
        Output(display_name="Trace URL", name="trace_url", method="evaluate"),
    ]

    def evaluate(self) -> Message:
        answer = self.answer
        context_data = self.context_data
        user_email = self.user_email if self.user_email else ""
        user_name = self.user_name if self.user_name else ""

        # Validate email if provided
        if user_email and not self.validate_email(user_email):
            raise ValueError(f"Invalid email address: {user_email}")

        langwatch.api_key = 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ0aW1lc3RhbXAiOjE3MjQ2MjkwNzYwMzAsInJhbmQiOjAuMzgzMDcyNTYxOTkxMTM4MzcsImlhdCI6MTcyNDYyOTA3Nn0.NUHUAmW7S7eb4B42WO1ioNpma4zvlcMSOmI7zCKc-xo'
        
        trace = langwatch.trace(
            metadata={
                "user_id": user_email,
                "user_name": user_name,
            },
        )
        
        context = self.process_context_data(context_data)
        rag_span = trace.span(type="rag", name="LangWatch Evaluator", input=context, contexts=[context], output=answer)
        rag_span.end()
        trace.send_spans()
        public_url = trace.share()
        
        print("See the trace at:", public_url)
        self.status = f"Submission successful! Results available at: {public_url}. Save this URL as your submission proof. Evaluation complete."
        return Message(text=public_url)

    def process_context_data(self, context_data: Union[str, Data]) -> str:
        if isinstance(context_data, Data):
            if hasattr(context_data, 'text'):
                return context_data.text
            elif isinstance(context_data.data, dict) and 'text' in context_data.data:
                return context_data.data['text']
            else:
                return str(context_data.data)
        elif isinstance(context_data, str):
            return context_data
        else:
            return str(context_data)

    def validate_email(self, email):
        pattern = r'^[\w\.-]+@[\w\.-]+\.\w+$'
        return re.match(pattern, email) is not None

    def validate_cpf(self, cpf):
        if not re.match(r'^(?!(\d)\1{10})\d{9}[\d]{2}$', cpf):
            return False
        total = sum(int(cpf[i]) * (10 - i) for i in range(9))
        check1 = (total * 10 % 11) % 10
        total = sum(int(cpf[i]) * (11 - i) for i in range(10))
        check2 = (total * 10 % 11) % 10
        return cpf[-2:] == f"{check1}{check2}"
